def factorial(n):
	result = 1
	for i in range(1, n + 1):
		result *= i
	return result

class Complex:
	def __init__(self, real, imag):
		self.real = real
		self.imag = imag
	def __add__(self, other):
		return Complex(self.real + other.real, self.imag + other.imag)
	def __sub__(self, other):
		return Complex(self.real - other.real, self.imag - other.imag)

from functools import wraps

def log_function(func):
